import java.util.HashMap;
import java.util.Map;

/**
 * 76. 最小覆盖子串
 *
 * @author avatarannappa
 * @version 1.0, 2020/1/18
 */
public class MinWindow76 {

    Map<Character, Integer> tMap = new HashMap<>();

    public String minWindow(String s, String t) {
        int left = 0;
        int right = 0;
        boolean find = false;
        int min = 0;
        int index = 0;
        if (s == null || t == null || s.length() < t.length()) {
            return "";
        }

        Map<Character, Integer> sMap = new HashMap<>();
        for (int i = 0; i < t.length(); i++) {
            char c = t.charAt(i);
            if (tMap.containsKey(c)) {
                tMap.put(c, tMap.get(c) + 1);
            } else {
                tMap.put(c, 1);
            }
        }

        int end = s.length();
        sMap.put(s.charAt(0), 1);
        while (left < end) {
            if (!containsT(sMap)) {
                right++;
                if (right >= end) {
                    break;
                }
                char rChar = s.charAt(right);
                if (sMap.containsKey(rChar)) {
                    sMap.put(rChar, sMap.get(rChar) + 1);
                } else {
                    sMap.put(rChar, 1);
                }
                continue;
            }
            if (find == false || right - left < min) {
                find = true;
                min = right - left;
                index = left;
            }
            char lChar = s.charAt(left);
            sMap.put(lChar, sMap.get(lChar) - 1);
            left++;
        }

        if (!find) {
            return "";
        }
        return s.substring(index, index + min + 1);
    }

    public boolean containsT(Map<Character, Integer> sMap) {
        for (Map.Entry<Character, Integer> entry : tMap.entrySet()) {
            if (!sMap.containsKey(entry.getKey()) || sMap.get(entry.getKey()) < entry.getValue()) {
                return false;
            }
        }
        return true;
    }

    public String minWindow2(String s, String t) {
        // 超出时间限制
        //""
        //""
        int index = 0;
        int len = 0;
        boolean find = false;
        if (s == null || t == null || s.length() < t.length()) {
            return "";
        }
        for (int i = 0; i < s.length(); i++) {
            for (int j = s.length() - 1; j >= i; j--) {
                String temp = s.substring(i, j + 1);
                if (find && temp.length() > len) {
                    continue;
                }
                if (contains(temp, t)) {
                    int l = j - i + 1;
                    if (!find) {
                        find = true;
                        index = i;
                        len = l;
                    } else if (l < len) {
                        find = true;
                        index = i;
                        len = l;
                    }
                }
            }
        }
        if (!find) {
            return "";
        }
        return s.substring(index, index + len);
    }

    public String minWindow1(String s, String t) {
        // 超出时间限制
        //""
        //"fjknwevk"
        int index = 0;
        int len = 0;
        boolean find = false;
        if (s == null || t == null || s.length() < t.length()) {
            return "";
        }
        for (int i = 0; i < s.length(); i++) {
            for (int j = s.length() - 1; j >= i; j--) {
                String temp = s.substring(i, j + 1);
                if (find && temp.length() > len) {
                    continue;
                }
                if (contains(temp, t)) {
                    int l = j - i + 1;
                    if (!find) {
                        find = true;
                        index = i;
                        len = l;
                    } else if (l < len) {
                        find = true;
                        index = i;
                        len = l;
                    }
                }
            }
        }
        if (!find) {
            return "";
        }
        return s.substring(index, index + len);
    }

    public boolean contains(String s, String t) {
        Map<Character, Integer> sMap = new HashMap<>();
        Map<Character, Integer> tMap = new HashMap<>();
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (sMap.containsKey(c)) {
                sMap.put(c, sMap.get(c) + 1);
            } else {
                sMap.put(c, 1);
            }
        }
        for (int i = 0; i < t.length(); i++) {
            char c = t.charAt(i);
            if (tMap.containsKey(c)) {
                tMap.put(c, tMap.get(c) + 1);
            } else {
                tMap.put(c, 1);
            }
        }
        for (Map.Entry<Character, Integer> entry : tMap.entrySet()) {
            if (!sMap.containsKey(entry.getKey()) || sMap.get(entry.getKey()) < entry.getValue()) {
                return false;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        /*String s ="";
        String t ="";
        */
        String s = "ADOBECODEBANC";
        String t = "ABC";
        System.out.println(s.length());
        System.out.println(t.length());
        System.out.println(new MinWindow76().minWindow(s, t));
    }
}
